import React, { useState } from 'react';
import './AboutUs.css';

const Team = () => {
  const [activeMember, setActiveMember] = useState(null);

  const teamMembers = [
    {
      id: 1,
      name: 'Imashi Thakshila',
      imgSrc: 'https://picsum.photos/id/1015/200/300',
    },
    {
      id: 2,
      name: 'Tharushi Dilisha',
      imgSrc: 'https://picsum.photos/id/1018/200/300',
    },
    {
      id: 3,
      name:'Akash Perera',
      imagSrc:'',
    },
    {
      id: 4,
      name:'Harini Tharupa',
      imgSrc:'',
    },
    {
      id: 5,
      name:'Nipuni Pathirana',
      imgSrc:'',
    },
    // Add more team members here
  ];

  const handleMouseEnter = (member) => {
    setActiveMember(member);
  };

  const handleMouseLeave = () => {
    setActiveMember(null);
  };

  return (
    <div className="team">
      <h2>Meet Our Team</h2>
      <div className="team-members">
        {teamMembers.map((member) => (
          <div
            key={member.id}
            className="team-member"
            onMouseEnter={() => handleMouseEnter(member)}
            onMouseLeave={handleMouseLeave}
          >
            <img src={member.imgSrc} alt={member.name} />
            <h3>{member.name}</h3>
            <p>{member.position}</p>
          </div>
        ))}
      </div>
      {activeMember && (
        <div className="active-member">
          <img src={activeMember.imgSrc} alt={activeMember.name} />
          <h3>{activeMember.name}</h3>
          <p>{activeMember.position}</p>
          <p>{activeMember.bio}</p>
        </div>
      )}
    </div>
  );
};

export default Team;
