import React, { useState } from 'react';
import './AboutUs.css';

const Team = () => {
  const [activeMember, setActiveMember] = useState(null);

  const teamMembers = [
    {
      id: 1,
      name: 'John Doe',
      position: 'Software Engineer',
      bio: 'John is a software engineer with 5 years of experience in web development. He specializes in React and Node.js and has worked on various projects ranging from small startups to large enterprises. In his free time, John enjoys hiking and playing guitar.',
      imgSrc: 'https://picsum.photos/id/1015/200/300',
    },
    {
      id: 2,
      name: 'Jane Smith',
      position: 'Product Manager',
      bio: 'Jane is a product manager with 7 years of experience in product management and project management. She has a proven track record of delivering high-quality products on time and within budget. Jane is also an expert in Agile methodologies and enjoys working with cross-functional teams.',
      imgSrc: 'https://picsum.photos/id/1018/200/300',
    },
    // Add more team members here
  ];

  const handleMouseEnter = (member) => {
    setActiveMember(member);
  };

  const handleMouseLeave = () => {
    setActiveMember(null);
  };

  return (
    <div className="team">
      <h2>Meet Our Team</h2>
      <div className="team-members">
        {teamMembers.map((member) => (
          <div
            key={member.id}
            className="team-member"
            onMouseEnter={() => handleMouseEnter(member)}
            onMouseLeave={handleMouseLeave}
          >
            <img src={member.imgSrc} alt={member.name} />
            <h3>{member.name}</h3>
            <p>{member.position}</p>
          </div>
        ))}
      </div>
      {activeMember && (
        <ActiveMember activeMember={activeMember} onMouseLeave={handleMouseLeave} />
      )}
    </div>
  );
};

const ActiveMember = ({ activeMember, onMouseLeave }) => {
  const [isScrolling, setIsScrolling] = useState(false);

  const handleScroll = (event) => {
    const { scrollTop, clientHeight, scrollHeight } = event.currentTarget;

    if (scrollTop + clientHeight === scrollHeight) {
      setIsScrolling(false);
    } else if (!isScrolling) {
      setIsScrolling(true);
    }
  };

  return (
    <div className="active-member" onMouseLeave={onMouseLeave} onScroll={handleScroll}>
      <img src={activeMember.imgSrc} alt={activeMember.name} />
      <h3>{activeMember.name}</h3>
      <p>{activeMember.position}</p>
      <p>{activeMember.bio}</p>
      <div className="scroll-indicator">
        <div className={`scroll-bar ${isScrolling ? 'scrolling' : ''}`} />
      </div>
    </div>
  );
};

export default Team;
