import React, { useEffect, useState } from 'react';
import './Navbar.css';
import logo from '../../assets/logo.png';
import menu_icon from '../../assets/menu-icon.png';
import LoginSignup from '../LoginSignup/LoginSignup'; // Import the LoginSignup component

import { Link } from 'react-scroll';

const Navbar = () => {
  const [sticky, setSticky] = useState(false);
  const [showLogin, setShowLogin] = useState(false); // State to manage visibility of login page

  useEffect(() => {
    window.addEventListener('scroll', () => {
      window.scrollY > 700 ? setSticky(true) : setSticky(false);
    });
  }, []);

  const [mobileMenu, setMobileMenu] = useState(false);
  const toggleMenu = () => {
    setMobileMenu(!mobileMenu);
  };

  return (
    <nav className={`container ${sticky ? 'dark-nav' : ''}`}>
      <img src={logo} alt='' className='logo' />
      <ul className={mobileMenu ? '' : 'hide-mobile-menu'}>
        <li>
          <Link to='hero' smooth={true} offset={0} duration={500}>
            Home
          </Link>
        </li>
        <li>
          <Link to='program' smooth={true} offset={-210} duration={500}>
            Program
          </Link>
        </li>
        <li>
          <Link to='about' smooth={true} offset={-80} duration={500}>
            About Us
          </Link>
        </li>
        <li>
          <Link to='contact' smooth={true} offset={0} duration={500}>
            Contact Us
          </Link>
        </li>
        <li>
          {/* Render the login page component when showLogin is true */}
          <button className='btn' onClick={() => setShowLogin(true)}>
            Log In
          </button>
        </li>
      </ul>
      <img
        src={menu_icon}
        alt=''
        className='menu-icon'
        onClick={toggleMenu}
      />
      {/* Render the login page component when showLogin is true */}
      {showLogin && <LoginSignup />}
    </nav>
  );
};

export default Navbar;
