// Navbar.js
import React from 'react';
import './Navbar.css';
import logo from '../../assets/logo.png';
import menu_icon from '../../assets/menu-icon.png';

import { Link } from 'react-scroll';

const Navbar = ({ setShowLogin }) => { // Destructure setShowLogin from props

  const [sticky, setStikey] = React.useState(false);

  React.useEffect(() => {
    window.addEventListener('scroll', () => {
      window.scrollY > 700 ? setStikey(true) : setStikey(false)
    })
  }, []);

  const [mobileMenu, setMobileMenu] = React.useState(false);
  const toggleMenu = () => {
    setMobileMenu(prevState => !prevState); // Toggle mobile menu
  }

  return (
    <nav className={`container ${sticky ? 'dark-nav' : ''}`}>
      <img src={logo} alt='' className='logo' />
      <ul className={mobileMenu ? '' : 'hide-mobile-menu'}>
        <li><Link to='hero' smooth={true} offset={0} duration={500}>Home</Link></li>
        <li><Link to='program' smooth={true} offset={-210} duration={500}>Program</Link></li>
        <li><Link to='about' smooth={true} offset={-80} duration={500}>About Us</Link></li>
        <li><Link to='contact' smooth={true} offset={0} duration={500}>Contact Us</Link></li>
        <li><button className='btn' onClick={() => setShowLogin(true)}>Login</button></li> {/* Call setShowLogin to show login page */}
      </ul>
      <img src={menu_icon} alt='' className='menu-icon' onClick={toggleMenu} />
    </nav>
  )
}

export default Navbar;
