import React, { useState } from 'react';
import './App.css';

const AboutPage = () => {
  const [showBio, setShowBio] = useState(null);

  const members = [
    {
      name: 'John Doe',
      image: 'https://via.placeholder.com/150',
      position: 'Software Engineer',
      bio: 'John is an experienced software engineer who specializes in building high-quality websites and applications.',
    },
    {
      name: 'Jane Smith',
      image: 'https://via.placeholder.com/150',
      position: 'UX Designer',
      bio: 'Jane is a skilled UX designer who focuses on creating user-friendly and visually appealing interfaces.',
    },
    // Add more members here
  ];

  const handleMouseOver = (index) => {
    setShowBio(index);
  };

  const handleMouseOut = () => {
    setShowBio(null);
  };

  return (
    <div className="about-page">
      <header className="about-header">
        <h1>About Us</h1>
      </header>
      <main className="about-main">
        <p>
          We are a team of passionate developers who specialize in creating
          beautiful and user-friendly websites and applications. Our mission is
          to help businesses grow by providing exceptional digital solutions.
        </p>
        <img
          src="https://upload.wikimedia.org/wikipedia/commons/thumb/b/b8/Group_people_icon.jpg/1200px-Group_people_icon.jpg"
          alt="BGimage"
          className="about-image"
        />
        <div className='about-members'>
          <h2>Meet the members</h2>
          <div className='member-container'>
            {members.map(({ name, image, position, bio }, index) => (
              <div
                className='member'
                key={index}
                onMouseOver={() => handleMouseOver(index)}
                onMouseOut={handleMouseOut}
              >
                <img
                  src={image}
                  alt={`${name} avatar`}
                  className='member-avatar'
                />
                <h3>{name}</h3>
                <p>{position}</p>
                {showBio === index && <p>{bio}</p>}
              </div>
            ))}
          </div>
        </div>
      </main>
    </div>
  );
};

export default AboutPage;
